<?phpnamespace Nemundo\Geo\Kml\Object;use Nemundo\Core\Type\Geo\GeoCoordinateAltitude;use Nemundo\Geo\Kml\Container\Placemark;use Nemundo\Geo\Kml\Element\Point;use Nemundo\Geo\Kml\Property\HtmlDescription;use Nemundo\Geo\Kml\Property\StyleUrl;class KmlMarker extends Placemark{    /**     * @var string     */    public $label;    /**     * @var string     */    public $description;    /**     * @var GeoCoordinateAltitude     */    public $geoCoordinate;    public $styleUrl;    public function getContent()    {        if ($this->styleUrl !== null) {            $styleUrl = new StyleUrl($this);            $styleUrl->value = '#' . $this->styleUrl;        }        if ($this->description !== null) {            $description = new HtmlDescription($this);            $description->value = $this->description;        }        if ($this->geoCoordinate !== null) {            $point = new Point($this);            $point->coordinate = $this->geoCoordinate;        }        return parent::getContent();    }}